import React from "react";
import { useState } from "react";
import "./product.css";
import Data from "../resources/data";
import Nav from "./Nav";

const Product = () => {
  const [data, setData] = useState(Data);
  const [ishidden, setishidden] = useState("true");

  return (
    <>
      <div className={ishidden ? "hidden" : null}>
        <button class="close-modal">&times;</button>
        <h1>I'm a modal window üòç</h1>
        <p>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip ex ea commodo consequat. Duis aute irure dolor in
          reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
          pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
          culpa qui officia deserunt mollit anim id est laborum.
        </p>
      </div>
      <div class="overlay hidden"></div>
      {data.map((curElem) => {
        return (
          <div className="Product">
            <img
              className="Product__image"
              src={curElem.otherImages[0]}
              alt=""
            />
            <div className="Product__text">
              <div className="Product__name">
                <span>
                  <strong className="Product__name">{curElem.name}</strong>
                </span>
              </div>

              <span>{curElem.description}</span>
              <p className="Product__price">
                <span className="Product__finalprice">
                  Rs.{curElem.finalPrice}
                </span>
                <span>
                  <strike>{curElem.strickPrice}</strike>
                </span>
                <span>{curElem.discount}% OFF</span>
              </p>
            </div>
          </div>
        );
      })}
    </>
  );
};

export default Product;
