import React from "react";
import "./singleproduct.css";
import { useCartContext } from "../cart_context";
const Singleproduct = () => {
  const { cart } = useCartContext();
  console.log(cart);
  return (
    <>
      <h1 className="modal__title">Welcome to the Cart!</h1>
      <div className="outercontainer">
        {cart.map((cur) => {
          return (
            <>
              <div className="modal__body">
                <div className="singleProduct__container">
                  <img
                    className="singleProduct__image"
                    src={cur.image}
                    alt=""
                  />
                  <div className="singleProduct__details">
                    <span className="singleProduct__title">{cur.itemname}</span>
                    <span className="singleProduct__description">
                      {cur.description}
                    </span>
                    <span className="singleProduct__finalprice">
                      Rs.{cur.finalprice}
                    </span>
                  </div>
                </div>
              </div>
            </>
          );
        })}
        <div className="Summary">
          <h1>Order Summary</h1>
        </div>
      </div>
    </>
  );
};

export default Singleproduct;
