import React from "react";
import "./filter.css";
import { Link } from "react-router-dom";
import data from "../resources/data";

const Filter = ({ setData }) => {
  const filterItem = (gender) => {
    const updatedList = data.filter((cur) => {
      return cur.gender === gender;
    });

    setData(updatedList);
  };
  return (
    <div className="Filter__container">
      <div className="Filter__gender">
        <h4>Gender:-</h4>
        <Link onClick={() => filterItem("M")}>MEN</Link>

        <br />
        <br />
        <Link onClick={() => filterItem("F")}>WOMEN</Link>
      </div>
      <div className="Fiter__categories">
        <h4>Categories:-</h4>
        <label htmlFor="white">White</label>
        <input type="radio" value="white" id="white" />
        <br />
        <br />
        <label htmlFor="folded">Folded Sleeves</label>
        <input type="radio" value="folded-sleeves" id="folded" />
      </div>
    </div>
  );
};

export default Filter;
