import React from "react";
import "./nav.css";
import icon from "../resources/icon.jpeg";
import cart from "../resources/shopping-cart-solid.svg";
import { useStateValue } from "../StateProvider";

const Nav = (index) => {
  const [{ basket }] = useStateValue();

  console.log(index);
  // console.log(basket);
  return (
    <>
      <nav className="header">
        {/* logo on the left */}
        {/* links to different pages */}
        {/* A search box */}
        {/* profile wishlist and bag on the right */}
        <div className="icon-holder">
          <img className="header__logo" src={icon} alt="Myntra_logo" />
        </div>
        <div className="links">
          <a href="#">Men</a>
          <a href="#">Women</a>
          <a href="#">Kids</a>
          <a href="#">Home & Living</a>
          <a href="#">Beauty</a>
          <a href="#">Studio</a>
        </div>

        <div className="header__searchbox">
          <input
            type="text"
            placeholder="Search for products, brands and more"
          />
        </div>

        <div className="cart-holder">
          <button>
            <a href="/cart">
              <img className="header_cart" src={cart} alt="" />
            </a>
            <span>{basket.length}</span>
          </button>
        </div>
      </nav>
    </>
  );
};

export default Nav;
